CREATE TABLE IF NOT EXISTS applications(user_id uuid NOT NULL, post_id INT NOT NULL, application_id INT GENERATED ALWAYS AS IDENTITY, message VARCHAR(511) NOT NULL DEFAULT '', accepted boolean NOT NULL DEFAULT FALSE, date_created TIMESTAMPTZ DEFAULT CURRENT_TIMESTAMP, PRIMARY KEY (application_id), CONSTRAINT fk_user FOREIGN KEY (user_id) REFERENCES users(user_id), CONSTRAINT fk_post FOREIGN KEY (post_id) REFERENCES posts(post_id));
