SET TIMEZONE='US/Eastern';
CREATE TABLE IF NOT EXISTS messages(sender uuid NOT NULL, message_id INT GENERATED ALWAYS AS IDENTITY, chatroom_id INT NOT NULL, message VARCHAR(1000) NOT NULL, is_deleted boolean NOT NULL DEFAULT FALSE, timestamp TIMESTAMPTZ DEFAULT now(), PRIMARY KEY (message_id), CONSTRAINT fk_user FOREIGN KEY (sender) REFERENCES users(user_id), CONSTRAINT fk_chatroom FOREIGN KEY (chatroom_id) REFERENCES chatrooms(chatroom_id));
