SET TIMEZONE='US/Eastern';
CREATE TYPE gender_types AS ENUM ('', 'male', 'female', 'other');
CREATE TABLE IF NOT EXISTS profiles( user_id uuid NOT NULL, profile_id INT GENERATED ALWAYS AS IDENTITY, profile_pic BOOLEAN NOT NULL DEFAULT FALSE, bio VARCHAR(2047) NOT NULL DEFAULT '', badge_list VARCHAR(2047) NOT NULL DEFAULT '', age INT NOT NULL DEFAULT 0, gender gender_types NOT NULL DEFAULT '', language VARCHAR(255) NOT NULL DEFAULT 'English', experience VARCHAR(1023) NOT NULL DEFAULT '', education VARCHAR(511) NOT NULL DEFAULT '', skills VARCHAR(511) NOT NULL DEFAULT '', service_types VARCHAR(511) NOT NULL DEFAULT '', age_groups VARCHAR(511) NOT NULL DEFAULT '', covid19 BOOLEAN NOT NULL DEFAULT FALSE, cpr BOOLEAN NOT NULL DEFAULT FALSE, first_aid BOOLEAN NOT NULL DEFAULT FALSE, smoker BOOLEAN NOT NULL DEFAULT FALSE, jobs_completed INT NOT NULL DEFAULT 0, rate_range VARCHAR(7) NOT NULL DEFAULT '', rating FLOAT NOT NULL DEFAULT 5, PRIMARY KEY (profile_id),  CONSTRAINT fk_user FOREIGN KEY (user_id) REFERENCES users(user_id));